            );
            
            $documento_iva_id = $this->Documento_iva_model->add_documento_iva($params);
            redirect('documento_iva/index');
        }
        else
        {
			$this->load->model('Documento_model');
			$data['all_documentos'] = $this->Documento_model->get_all_documentos();
            
