        }
    }  

    /*
     * Editing a documento_iva
     */
    function edit($id)
    {   
        // check if the documento_iva exists before trying to edit it
        $data['documento_iva'] = $this->Documento_iva_model->get_documento_iva($id);
        
        if(isset($data['documento_iva']['id']))
        {
            $this->load->library('form_validation');

			$this->form_validation->set_rules('monto','Monto','required');
			$this->form_validation->set_rules('porcentaje','Porcentaje','required');
		
			if($this->form_validation->run())     
