     */
    function remove($id)
    {
        $documento_iva = $this->Documento_iva_model->get_documento_iva($id);

        // check if the documento_iva exists before trying to delete it
        if(isset($documento_iva['id']))
        {
            $this->Documento_iva_model->delete_documento_iva($id);
